$(document).ready(function(){let $range=$(".js-range-slider"),$inputFrom=$(".js-input-from"),$inputTo=$(".js-input-to"),instance,min=0,max=1e4,from=0,to=1e4;$range.ionRangeSlider({skin:"round",type:"double",min:min,max:max,from:from,to:to,onStart:updateInputs,onChange:updateInputs});function updateInputs(data){from=data.from;to=data.to;$inputFrom.val(from+"$");$inputTo.val(to+"$")}instance=$range.data("ionRangeSlider");$inputFrom.on("keyup",function(){let val=$(this).val();if(val<min){val=min}else if(val>to){val=to}instance.update({from:val})});$inputTo.on("keyup",function(){let val=$(this).val();if(val<from){val=from}else if(val>max){val=max}instance.update({to:val})});$("select, input:checkbox, input:radio").styler();$.getJSON("assets/data/service.json",function(json){$(".card_item").pagination({dataSource:json,pageSize:5,callback:function(data,pagination){$("#product_list").empty();$.each(data,function(i,f){$("#product_list").append('<li><a href="javascript:void(0)"><img src="${f.image}" alt=""><i class="fa-linkedin-in"></a></li>')})}})})});